import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './home.css';

function Home() {
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const username = queryParams.get('username');
    return (
        <div className='container'>
            <div className='bg'></div>
            <div className='content'>
                <div className='row balance'>
                    <h1>Hello : {username}</h1>
                    <div className='blance-number'>
                        <span className='currency'>$</span>
                        <span className='number'>500.00</span>
                    </div>
                    <div className='user'>
                        <Link to='/'><i class="fa-solid fa-arrow-right-from-bracket"></i> Log out</Link>
                    </div>
                </div>
                <div className='row'>
                    <div className='link-btn'>
                        <Link to='/deposit'><i class="fa-solid fa-money-bill-transfer"></i> Deposit</Link>
                        <Link to='/transfer'><i class="fa-solid fa-arrow-right-arrow-left"></i> Transfer</Link>
                        <Link to='/withdraw'><i class="fa-solid fa-money-bill-transfer"></i> Withdraw</Link>
                    </div>
                </div>
                <div className='row list'>
                    <ul>
                        {transaction.map((transaction) => (
                            <li className='list-item'>
                                <span className='list-type'>{transaction.type}</span>
                                <div className='info'>
                                    <span className='list-date'>{transaction.date} - {transaction.time}</span>
                                    {transaction.amount < 0 ? <span style={{color: '#f50e40'}} className='list-number'>${transaction.amount}</span> : <span style={{color: '#00a854'}} className='list-number'>${transaction.amount}</span>}
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    )
}


export default Home;
