import React, { useState } from 'react';

import { Link } from 'react-router-dom';

function Deposit() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password }),
            });
    
            if (response.ok) {
                const data = await response.json();
                console.log("Token:", data.token); // Log the token
                localStorage.setItem('token', data.token);
                window.location.href = '/home';
            } else {
                const errorData = await response.json();
                setError(errorData.message);
            }
        } catch (error) {
            setError('An unexpected error occurred. Please try again later.');
        }
    };
    return (
        <div className='container'>
            <div className='form-area'>
                <h1><i class="fa-solid fa-money-bill-transfer"></i> Deposit</h1>
                <form onSubmit={handleSubmit}>
                    <div className='row input-number'>
                        <input type='number' placeholder='0.00' name='input-number'></input>
                    </div>
                    <div className='row'>
                        <div className='link-btn'>
                            <Link to='/home' className='back-btn'><i class="fa-solid fa-chevron-left"></i> Back</Link>
                            <button type='submit'>Confirm</button>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    )
}


export default Deposit;
