import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import './home.css';

const Home = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const history = useHistory();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password }),
            });
            if (response.ok) {
                const data = await response.json();
                localStorage.setItem('token', data.token);
                history.push(`/profile?token=${data.token}&userId=${username}`);
            } else {
                const { message } = await response.json();
                setError(message);
            }
        } catch (error) {
            console.error('Login error:', error);
            setError('An error occurred during login.');
        }
    };

    return (
        <div className='container'>
            <div className='bg'></div>
            <div className='content'>
                <div className='row balance'>
                    <h1>Hello : {username}</h1>
                    <div className='blance-number'>
                        <span className='currency'>$</span>
                        <span className='number'>500.00</span>
                    </div>
                    <div className='user'>
                        <Link to='/'><i className="fa-solid fa-arrow-right-from-bracket"></i> Log out</Link>
                    </div>
                </div>
                <div className='row'>
                    <div className='link-btn'>
                        <Link to='/deposit'><i className="fa-solid fa-money-bill-transfer"></i> Deposit</Link>
                        <Link to='/transfer'><i className="fa-solid fa-arrow-right-arrow-left"></i> Transfer</Link>
                        <Link to='/withdraw'><i className="fa-solid fa-money-bill-transfer"></i> Withdraw</Link>
                    </div>
                </div>
                <div className='row'>
                    <form onSubmit={handleLogin}>
                        <input
                            type='text'
                            placeholder='Username'
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                        <input
                            type='password'
                            placeholder='Password'
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                        <button type='submit'>Login</button>
                    </form>
                    {error && <div className='error'>{error}</div>}
                </div>
            </div>
        </div>
    );
};

export default Home;
