import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';

function Deposit() {
    const [amount, setAmount] = useState('');
    const history = useHistory();

    const handleSubmit = async (event) => {
        event.preventDefault();
        const token = localStorage.getItem('token');

        if (!token) {
            console.log('No token found');
            // Redirect to login or handle the no token case
            history.push('/login');
            return;
        }

        try {
            const response = await fetch('/deposit', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ amount }) // Update as per your backend requirements
            });

            const data = await response.json();
            if (response.ok) {
                alert(`Deposit Successful: ${data.message}`);
                // Redirect or update UI as needed
                history.push('/home'); // Example redirection after successful deposit
            } else {
                alert(`Deposit Failed: ${data.message}`);
            }
        } catch (error) {
            console.error('Error during deposit:', error);
            alert('Error: ' + error.message);
        }
    };

    return (
        <div className='container'>
            <div className='form-area'>
                <h1><i className="fa-solid fa-money-bill-transfer"></i> Deposit</h1>
                <form onSubmit={handleSubmit}>
                    <div className='row input-number'>
                        <input 
                            type='number' 
                            placeholder='0.00' 
                            value={amount}
                            onChange={(e) => setAmount(e.target.value)}
                            name='input-number'
                        />
                    </div>
                    <div className='row'>
                        <div className='link-btn'>
                            <Link to='/home' className='back-btn'><i className="fa-solid fa-chevron-left"></i> Back</Link>
                            <button type='submit'>Confirm</button>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default Deposit;
